public class Main {
    public static int[] findNumbers(int[] numbers, int target) {
        int pLeft = 0;  // 
        int pRight = numbers.length - 1; // 

        while (pLeft < pRight) {
            int sum = numbers[pLeft] + numbers[pRight];
            if (sum == target) {
                return new int[]{numbers[pLeft], numbers[pRight]}; // 
            } else if (sum < target) {
                pLeft++; // 
            } else {
                pRight--; // 
            }
        }

        return new int[]{-1, -1};
    }

    public static void main(String[] args) {
        int[] results = findNumbers(new int[]{2, 4, 5, 6, 12}, 10);
